  
var jairo = require('../index.js');
var fs = require('fs');

var userDefinedPrefixes = [
        { 'prefix' : 'rdf'         , 'uri' : 'http://www.w3.org/1999/02/22-rdf-syntax-ns#'   },
        { 'prefix' : 'rdfs'        , 'uri' : 'http://www.w3.org/2000/01/rdf-schema#'         },
        { 'prefix' : 'dc'        , 'uri' : 'http://purl.org/dc/elements/1.1/'         },
        { 'prefix' : 'dcterms'        , 'uri' : 'http://purl.org/dc/terms/'         },
        { 'prefix' : 'sysml'       , 'uri' : 'http://omg/org/sysml/1.3/'                     },
        { 'prefix' : 'sysml_block'         , 'uri' : 'http://www.example.com/sysml_block/' } 
    ];

fs.readFile( '../files/test.rdf', 'utf8', function (err, fileContent) {
	if (err) throw err;
	jairo.parse( 'rdfxml' , fileContent , userDefinedPrefixes , function( parsedData ){
		console.log( parsedData );
		jairo.printHumanReadbleResult( parsedData );
	});
});
